import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Paper,
  List,
  ListItem as MuiListItem,
  ListItemText,
  ListItemAvatar,
  Avatar,
  TextField,
  IconButton,
  Divider,
  Grid as MuiGrid,
  Card,
  CardContent,
  Badge,
  InputAdornment
} from '@mui/material';
import {
  Send as SendIcon,
  Search as SearchIcon,
  Circle as CircleIcon
} from '@mui/icons-material';
import { styled } from '@mui/material/styles';
import type { Theme } from '@mui/material/styles';
import type { GridProps } from '@mui/material/Grid';
import type { ListItemButtonProps } from '@mui/material/ListItemButton';

interface Message {
  id: string;
  senderId: string;
  text: string;
  timestamp: string;
  isRead: boolean;
}

interface Contact {
  id: string;
  name: string;
  company: string;
  avatar: string;
  lastMessage: string;
  lastMessageTime: string;
  unreadCount: number;
  online: boolean;
}

type StyledGridProps = GridProps & {
  item?: boolean;
  xs?: number | boolean;
  md?: number | boolean;
  lg?: number | boolean;
};

const StyledGrid = styled(MuiGrid, {
  shouldForwardProp: (prop) => !['item', 'xs', 'md', 'lg'].includes(prop as string)
})<StyledGridProps>(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column'
}));

const StyledListItem = styled(MuiListItem)<ListItemButtonProps>(({ theme }) => ({
  cursor: 'pointer',
}));

const CommunicationsPage: React.FC = () => {
  // Demo data
  const [contacts] = useState<Contact[]>([
    {
      id: '1',
      name: 'John Smith',
      company: 'Venture Partners',
      avatar: 'VP',
      lastMessage: 'Looking forward to our meeting tomorrow',
      lastMessageTime: '10:30 AM',
      unreadCount: 2,
      online: true
    },
    {
      id: '2',
      name: 'Sarah Johnson',
      company: 'Growth Capital Fund',
      avatar: 'GC',
      lastMessage: 'Could you share more details about your revenue model?',
      lastMessageTime: 'Yesterday',
      unreadCount: 0,
      online: false
    },
    {
      id: '3',
      name: 'Michael Chen',
      company: 'Impact Ventures',
      avatar: 'IV',
      lastMessage: "Great presentation! Let's schedule a follow-up",
      lastMessageTime: 'Yesterday',
      unreadCount: 1,
      online: true
    }
  ]);

  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      senderId: '1',
      text: "Hi, I reviewed your pitch deck and I'm quite impressed with your approach.",
      timestamp: '10:00 AM',
      isRead: true
    },
    {
      id: '2',
      senderId: 'me',
      text: "Thank you! We've been working hard on refining our strategy.",
      timestamp: '10:15 AM',
      isRead: true
    },
    {
      id: '3',
      senderId: '1',
      text: 'Looking forward to our meeting tomorrow',
      timestamp: '10:30 AM',
      isRead: false
    }
  ]);

  const [selectedContact, setSelectedContact] = useState<Contact | null>(contacts[0]);
  const [newMessage, setNewMessage] = useState('');
  const [searchQuery, setSearchQuery] = useState('');

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      const currentTime = new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
      const newMessageObj: Message = {
        id: (messages.length + 1).toString(),
        senderId: 'me',
        text: newMessage.trim(),
        timestamp: currentTime,
        isRead: true
      };

      setMessages(prevMessages => [...prevMessages, newMessageObj]);
      setNewMessage('');
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <Box sx={{ height: 'calc(100vh - 100px)', p: 3 }}>
      <StyledGrid container spacing={2} sx={{ height: '100%' }}>
        {/* Left sidebar - Contacts */}
        <StyledGrid item xs={12} md={4} lg={3}>
          <Card sx={{ height: '100%' }}>
            <CardContent sx={{ p: 2, height: '100%' }}>
              <Typography variant="h6" gutterBottom>
                Messages
              </Typography>
              <TextField
                fullWidth
                size="small"
                placeholder="Search conversations..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                sx={{ mb: 2 }}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SearchIcon />
                    </InputAdornment>
                  ),
                }}
              />
              <List sx={{ 
                overflow: 'auto', 
                height: 'calc(100% - 120px)',
                '&::-webkit-scrollbar': {
                  width: '0.4em'
                },
                '&::-webkit-scrollbar-track': {
                  background: '#f1f1f1'
                },
                '&::-webkit-scrollbar-thumb': {
                  backgroundColor: '#888'
                }
              }}>
                {contacts
                  .filter(contact => 
                    contact.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                    contact.company.toLowerCase().includes(searchQuery.toLowerCase())
                  )
                  .map((contact) => (
                    <React.Fragment key={contact.id}>
                      <StyledListItem 
                        component="div"
                        onClick={() => setSelectedContact(contact)}
                        selected={selectedContact?.id === contact.id}
                      >
                        <ListItemAvatar>
                          <Badge
                            overlap="circular"
                            anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
                            variant="dot"
                            sx={{
                              '& .MuiBadge-badge': {
                                backgroundColor: contact.online ? '#44b700' : '#ccc',
                              },
                            }}
                          >
                            <Avatar sx={{ bgcolor: 'primary.main' }}>
                              {contact.avatar}
                            </Avatar>
                          </Badge>
                        </ListItemAvatar>
                        <ListItemText
                          primary={
                            <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                              <Typography variant="subtitle2">{contact.name}</Typography>
                              <Typography variant="caption" color="text.secondary">
                                {contact.lastMessageTime}
                              </Typography>
                            </Box>
                          }
                          secondary={
                            <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                              <Typography
                                variant="body2"
                                color="text.secondary"
                                sx={{
                                  overflow: 'hidden',
                                  textOverflow: 'ellipsis',
                                  whiteSpace: 'nowrap',
                                  maxWidth: '180px'
                                }}
                              >
                                {contact.lastMessage}
                              </Typography>
                              {contact.unreadCount > 0 && (
                                <Badge
                                  badgeContent={contact.unreadCount}
                                  color="primary"
                                  sx={{ ml: 1 }}
                                />
                              )}
                            </Box>
                          }
                        />
                      </StyledListItem>
                      <Divider component="li" />
                    </React.Fragment>
                  ))}
              </List>
            </CardContent>
          </Card>
        </StyledGrid>

        {/* Right side - Chat */}
        <StyledGrid item xs={12} md={8} lg={9}>
          <Card sx={{ height: '100%' }}>
            <CardContent sx={{ 
              p: 2, 
              height: '100%', 
              display: 'flex', 
              flexDirection: 'column'
            }}>
              {selectedContact ? (
                <>
                  {/* Chat Header */}
                  <Box sx={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    pb: 2, 
                    borderBottom: 1,
                    borderColor: 'divider'
                  }}>
                    <Badge
                      overlap="circular"
                      anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
                      variant="dot"
                      sx={{
                        '& .MuiBadge-badge': {
                          backgroundColor: selectedContact.online ? '#44b700' : '#ccc',
                        },
                      }}
                    >
                      <Avatar sx={{ bgcolor: 'primary.main', mr: 2 }}>
                        {selectedContact.avatar}
                      </Avatar>
                    </Badge>
                    <Box>
                      <Typography variant="subtitle1">
                        {selectedContact.name}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {selectedContact.company}
                      </Typography>
                    </Box>
                  </Box>

                  {/* Messages */}
                  <Box sx={{ 
                    flexGrow: 1, 
                    overflow: 'auto',
                    my: 2,
                    px: 2,
                    '&::-webkit-scrollbar': {
                      width: '0.4em'
                    },
                    '&::-webkit-scrollbar-track': {
                      background: '#f1f1f1'
                    },
                    '&::-webkit-scrollbar-thumb': {
                      backgroundColor: '#888'
                    }
                  }}>
                    {messages.map((message) => (
                      <Box
                        key={message.id}
                        sx={{
                          display: 'flex',
                          justifyContent: message.senderId === 'me' ? 'flex-end' : 'flex-start',
                          mb: 2
                        }}
                      >
                        <Paper
                          sx={{
                            p: 2,
                            backgroundColor: message.senderId === 'me' ? 'primary.main' : 'grey.100',
                            color: message.senderId === 'me' ? 'white' : 'inherit',
                            maxWidth: '70%'
                          }}
                        >
                          <Typography variant="body1">
                            {message.text}
                          </Typography>
                          <Typography 
                            variant="caption" 
                            sx={{ 
                              display: 'block',
                              textAlign: 'right',
                              mt: 0.5,
                              opacity: 0.8
                            }}
                          >
                            {message.timestamp}
                            {message.senderId === 'me' && (
                              <CircleIcon 
                                sx={{ 
                                  ml: 0.5, 
                                  fontSize: 12,
                                  color: message.isRead ? '#44b700' : 'inherit'
                                }} 
                              />
                            )}
                          </Typography>
                        </Paper>
                      </Box>
                    ))}
                  </Box>

                  {/* Message Input */}
                  <Box sx={{ 
                    display: 'flex', 
                    gap: 1,
                    pt: 2,
                    borderTop: 1,
                    borderColor: 'divider'
                  }}>
                    <TextField
                      fullWidth
                      multiline
                      maxRows={4}
                      placeholder="Type a message..."
                      value={newMessage}
                      onChange={(e) => setNewMessage(e.target.value)}
                      onKeyPress={handleKeyPress}
                      size="small"
                    />
                    <IconButton 
                      color="primary" 
                      onClick={handleSendMessage}
                      disabled={!newMessage.trim()}
                    >
                      <SendIcon />
                    </IconButton>
                  </Box>
                </>
              ) : (
                <Box sx={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  justifyContent: 'center',
                  height: '100%'
                }}>
                  <Typography variant="h6" color="text.secondary">
                    Select a conversation to start messaging
                  </Typography>
                </Box>
              )}
            </CardContent>
          </Card>
        </StyledGrid>
      </StyledGrid>
    </Box>
  );
};

export default CommunicationsPage;
