import React from 'react';
import { Box, Typography, Card, CardContent, List, ListItem, ListItemText } from '@mui/material';
import { useTheme } from '@mui/material/styles';

const StatsCard: React.FC<{ title: string; value: number; }> = ({ title, value }) => {
  const theme = useTheme();
  return (
    <Card sx={{ height: '100%', backgroundColor: theme.palette.background.paper, transition: 'transform 0.3s', '&:hover': { transform: 'scale(1.05)' } }}>
      <CardContent>
        <Typography variant="h6" component="div" color="primary">
          {title}
        </Typography>
        <Typography variant="h4" component="div" sx={{ fontWeight: 'bold', color: theme.palette.secondary.main }}>
          {value}
        </Typography>
      </CardContent>
    </Card>
  );
};

const InvestorDashboard: React.FC = () => {
  const theme = useTheme();

  return (
    <Box sx={{ p: 3, backgroundColor: theme.palette.grey[100], minHeight: '100vh' }}>
      <Typography variant="h4" gutterBottom color="primary">
        Investor Dashboard
      </Typography>
      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 3 }}>
        <Box sx={{ flex: { xs: '1 1 100%', sm: '1 1 calc(50% - 12px)', md: '1 1 calc(25% - 18px)' } }}>
          <StatsCard title="Portfolio Companies" value={8} />
          <List>
            <ListItem><ListItemText primary="Company A" /></ListItem>
            <ListItem><ListItemText primary="Company B" /></ListItem>
          </List>
        </Box>
        <Box sx={{ flex: { xs: '1 1 100%', sm: '1 1 calc(50% - 12px)', md: '1 1 calc(25% - 18px)' } }}>
          <StatsCard title="Investment Total" value={2000000} />
          <Typography variant="body1" color="textSecondary">Breakdown of investments...</Typography>
        </Box>
        <Box sx={{ flex: { xs: '1 1 100%', sm: '1 1 calc(50% - 12px)', md: '1 1 calc(25% - 18px)' } }}>
          <StatsCard title="Upcoming Meetings" value={5} />
          <List>
            <ListItem><ListItemText primary="Meeting with Company A" /></ListItem>
            <ListItem><ListItemText primary="Meeting with Company B" /></ListItem>
          </List>
        </Box>
        <Box sx={{ flex: { xs: '1 1 100%', sm: '1 1 calc(50% - 12px)', md: '1 1 calc(25% - 18px)' } }}>
          <StatsCard title="Unread Messages" value={10} />
          <Typography variant="body1" color="textSecondary">Recent messages...</Typography>
        </Box>
      </Box>
    </Box>
  );
};

export default InvestorDashboard;
