import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { StartupUser, InvestorUser, UserRole, DashboardStats } from '../types';
import { authService } from '../services/auth.service';
import { dataService } from '../services/data.service';
import { User } from '@supabase/supabase-js';

// Mock data for development - in production this would come from API
import { mockStartups, mockInvestors } from '../utils/mockData';

type AuthUser = StartupUser | InvestorUser | null;

interface AppContextType {
  user: User | null;
  loading: boolean;
  isAuthenticated: boolean;
  login: (email: string, password: string) => Promise<boolean>;
  register: (email: string, password: string, userDetails: Partial<StartupUser | InvestorUser>) => Promise<boolean>;
  logout: () => Promise<void>;
  updateProfile: (updates: Partial<User>) => Promise<boolean>;
  getDashboardStats: () => Promise<DashboardStats | null>;
  setUser: (user: User | null) => void;
}

const AppContext = createContext<AppContextType | undefined>(undefined);

// Default dashboard stats
const defaultStats: DashboardStats = {
  totalMatches: 0,
  profileViews: 0,
  meetingsScheduled: 0,
  activeDeals: 0,
  fundingProgress: {
    target: 0,
    current: 0,
    percentage: 0
  },
  investmentCommitments: 0,
  openDeals: 0,
  potentialInvestments: 0,
};

export const AppProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [isAuthenticated, setIsAuthenticated] = useState<boolean>(false);

  // Check for existing session on initial load
  useEffect(() => {
    const checkSession = async () => {
      try {
        setLoading(true);
        const { success, data } = await authService.getSession();

        if (success && data && data.session) {
          const userResult = await authService.getUser();
          if (userResult.success && userResult.data) {
            setUser(userResult.data);
            setIsAuthenticated(true);
          }
        }
      } catch (error) {
        console.error('Session check error:', error);
      } finally {
        setLoading(false);
      }
    };

    checkSession();
  }, []);

  // Login function
  const login = async (email: string, password: string): Promise<boolean> => {
    try {
      setLoading(true);
      const { success, data, error } = await authService.signIn(email, password);

      if (success && data) {
        setUser(data);
        setIsAuthenticated(true);
        return true;
      }

      console.error('Login error:', error);
      return false;
    } catch (error) {
      console.error('Login error:', error);
      return false;
    } finally {
      setLoading(false);
    }
  };

  // Register function
  const register = async (email: string, password: string, userDetails: Partial<StartupUser | InvestorUser>): Promise<boolean> => {
    try {
      setLoading(true);
      const { success, error } = await authService.signUp(email, password, userDetails);

      if (success) {
        // On success, auto-login
        return await login(email, password);
      }

      console.error('Registration error:', error);
      return false;
    } catch (error) {
      console.error('Registration error:', error);
      return false;
    } finally {
      setLoading(false);
    }
  };

  // Logout function
  const logout = async (): Promise<void> => {
    try {
      setLoading(true);
      await authService.signOut();
      setUser(null);
      setIsAuthenticated(false);
    } catch (error) {
      console.error('Logout error:', error);
    } finally {
      setLoading(false);
    }
  };

  // Update profile function
  const updateProfile = async (updates: Partial<User>): Promise<boolean> => {
    try {
      setUser(prev => prev ? { ...prev, ...updates } : null);
      return true;
    } catch (error) {
      console.error('Error updating profile:', error);
      return false;
    }
  };

  // Get dashboard stats
  const getDashboardStats = async (): Promise<DashboardStats | null> => {
    try {
      // Mock data for development
      return {
        totalInvestors: 150,
        totalStartups: 200,
        totalMatches: 50,
        totalDeals: 25
      };
    } catch (error) {
      console.error('Error fetching dashboard stats:', error);
      return null;
    }
  };

  // Context value
  const contextValue = {
    user,
    loading,
    isAuthenticated,
    login,
    register,
    logout,
    updateProfile,
    getDashboardStats,
    setUser
  };

  return (
    <AppContext.Provider value={contextValue}>
      {children}
    </AppContext.Provider>
  );
};

export const useAppContext = (): AppContextType => {
  const context = useContext(AppContext);
  if (context === undefined) {
    throw new Error('useAppContext must be used within an AppProvider');
  }
  return context;
};

export default AppContext;
